SELECT
(CASE RPC.TIPO WHEN 'E' THEN SUBSTRING(EP.CODIGO_FISCAL FROM 1 FOR 4) ELSE
CASE RPC.TIPO WHEN 'N' THEN SUBSTRING(NP.CODIGO_FISCAL FROM 1 FOR 4) ELSE
CASE WHEN RPC.TIPO = 'M' AND M.SITUACAO_CUPOM = 'FF' AND NOT UPPER(M.SERIE) STARTING WITH 'EMISS' THEN (SELECT VALOR FROM CONFIGURACOES WHERE CAMPO = 'FISCAL_CUPOM_ST') ELSE
CASE WHEN RPC.TIPO = 'M' AND M.SITUACAO_CUPOM = 'NN' AND NOT UPPER(M.SERIE) STARTING WITH 'EMISS' THEN (SELECT VALOR FROM CONFIGURACOES WHERE CAMPO = 'FISCAL_CUPOM_NN') ELSE
CASE WHEN RPC.TIPO = 'M' AND M.SITUACAO_CUPOM = 'II' AND NOT UPPER(M.SERIE) STARTING WITH 'EMISS' THEN (SELECT VALOR FROM CONFIGURACOES WHERE CAMPO = 'FISCAL_CUPOM_II') ELSE
CASE WHEN (RPC.TIPO = 'M' AND NOT UPPER(M.SERIE) STARTING WITH 'EMISS') THEN (SELECT VALOR FROM CONFIGURACOES WHERE CAMPO = 'FISCAL_CUPOM') ELSE
CASE WHEN RPC.TIPO = 'M' THEN M.CODIGO_FISCAL END END END end end end end) AS CODIGO_FISCAL,


(CASE RPC.TIPO WHEN 'E' THEN EP.total ELSE
CASE RPC.TIPO WHEN 'N' THEN NP.total ELSE
CASE WHEN RPC.TIPO = 'M' AND M.TIPO = 'E' THEN M.total ELSE
CASE WHEN RPC.TIPO = 'M' AND M.TIPO = 'S' THEN M.total_venda END END END end) AS TOTAL_ITEM,


RPC.pis_cst,
RPC.PIS_VBC BCPIS, RPC.PIS_VPIS VPIS,
RPC.COFINS_VBC BCCOFINS, RPC.COFINS_VCOFINS VCOFINS

  FROM RELACAO_PIS_COFINS RPC

  LEFT JOIN ENTRADAPRODUTOS EP ON EP.RELATIVO = RPC.RELATIVO AND RPC.TIPO = 'E'
  LEFT JOIN NOTAPRODUTOS NP ON NP.RELATIVO = RPC.RELATIVO AND RPC.TIPO = 'N'
  LEFT JOIN NOTAS N ON N.NUMERO = NP.NUMERO AND N.EMPRESA = NP.EMPRESA AND N.SERIE = NP.SERIE AND RPC.TIPO = 'N'
  LEFT JOIN MOVIMENTO M ON M.RELATIVO = CAST(RPC.RELATIVO AS VARCHAR(8)) AND RPC.TIPO = 'M'
 WHERE RPC.DATA BETWEEN :DINI AND :DFIM
   AND (((N.CANCELADA <> 'S') AND (N.GERADO_NFE <> 'D')) or RPC.tipo = 'M' or RPC.tipo = 'E' or RPC.TIPO = 'N')
   AND RPC.EMPRESA = :EMPRESA

order by RPC.pis_cst, CODIGO_FISCAL

